import React, { Component } from 'react'
// import { movies } from './getMovies'

export default class Favorites extends Component {
    constructor() {
        super();
        this.state = {
            genres: [],
            currgen: 'All Genres',
            movies: [],
            currText: '',
            limit: 5,
            currPage: 1
        };
    }
    componentDidMount() {
        let genreids = {
            28: 'Action', 12: 'Adventure', 16: 'Animation', 35: 'Comedy', 80: 'Crime', 99: 'Documentary', 18: 'Drama', 10751: 'Family', 14: 'Fantasy', 36: 'History',
            27: 'Horror', 10402: 'Music', 9648: 'Mystery', 10749: 'Romance', 878: 'Sci-Fi', 10770: 'TV', 53: 'Thriller', 10752: 'War', 37: 'Western'
        };
        let data = JSON.parse(localStorage.getItem("movie2") || "[]")
        let temp = []

        data.forEach((movieObj) => {
            if (!temp.includes(genreids[movieObj.genre_ids[0]])) {
                temp.push(genreids[movieObj.genre_ids[0]]);
            }
        })
        temp.unshift('All Genres');
        this.setState({
            genres: [...temp],
            movies: [...data]
        })
    }
    handleGenreChange = (genre) => {
        this.setState({
            currgen: genre
        })
    }

    sortPopularityDesc = () => {
        let temp = this.state.movies;
        temp.sort(function (objA, objB) {
            return objB.popularity - objA.popularity
        })
        this.setState({
            movies: [...temp]
        })
    }
    sortPopularityAsc = () => {
        let temp = this.state.movies;
        temp.sort(function (objA, objB) {
            return objA.popularity - objB.popularity
        })
        this.setState({
            movies: [...temp]
        })
    }

    sortRatingDesc = () => {
        let temp = this.state.movies;
        temp.sort(function (objA, objB) {
            return objB.vote_average - objA.vote_average
        })
        this.setState({
            movies: [...temp]
        })
    }
    sortRatingAsc = () => {
        let temp = this.state.movies;
        temp.sort(function (objA, objB) {
            return objA.vote_average - objB.vote_average
        })
        this.setState({
            movies: [...temp]
        })
    }
    handlePageChange = (page) => {
        this.setState({
            currPage: page
        })
    }
    handleDelete = (id) => {
        let newarr = [];
        newarr = this.state.movies.filter((movieObj) => movieObj.id != id)
        this.setState({
            movies: [...newarr]
        })
        localStorage.setItem("movies-app", JSON.stringify(newarr))
    }
    render() {

        let genreids = {
            28: 'Action', 12: 'Adventure', 16: 'Animation', 35: 'Comedy', 80: 'Crime', 99: 'Documentary', 18: 'Drama', 10751: 'Family', 14: 'Fantasy', 36: 'History',
            27: 'Horror', 10402: 'Music', 9648: 'Mystery', 10749: 'Romance', 878: 'Sci-Fi', 10770: 'TV', 53: 'Thriller', 10752: 'War', 37: 'Western'
        };
        let filterarr = [];

        if (this.state.currText == '') {
            filterarr = this.state.movies
        }
        else {
            filterarr = this.state.movies.filter((movieObj) => {
                let title = movieObj.original_title.toLowerCase();
                return title.includes(this.state.currText.toLocaleLowerCase())
            })
        }


        // if(this.state.currgen==='All Genres'){
        //     filterarr=this.state.movies
        // }
        if (this.state.currgen != 'All Genres') {
            filterarr = this.state.movies.filter((movieObj) =>
                genreids[movieObj.genre_ids[0]] == this.state.currgen
                // let title = movieObj.original_title.toLowerCase();
                // return title.includes(this.state.currText.toLowerCase())
            )
        }

        let pages = Math.ceil(filterarr.length / this.state.limit);
        let pagesarr = [];
        for (let i = 1; i <= pages; i++) {
            pagesarr.push(i);
        }
        let si = (this.state.currPage - 1) * this.state.limit;
        let ei = si + Number(this.state.limit);
        filterarr = filterarr.slice(si, ei);


        return (
            <div>
                <>
                    <div className='main'>
                        <div className='row'>
                            <div className='col-lg-3 col-sm-12'>
                                <ul class="list-group favorites-genres">
                                    {
                                        this.state.genres.map((genre) => (

                                            this.state.currgen == genre ?
                                                <li class="list-group-item" style={{ background: '#00e6e6', color: 'white', fontWeight: 'bold' }} >{genre}</li> :

                                                <li class="list-group-item" style={{ background: 'white', color: '#00e6e6' }} onClick={() => this.handleGenreChange(genre)} >{genre}</li>
                                        ))
                                    }
                                </ul>
                            </div>
                            <div className='col-lg-9 favorites-table col-sm-12'>


                                <div className='row'>
                                    <input type="text" className='input-group-text col' placeholder='Search' value={this.state.currText} onChange={(e) => this.setState({ currText: e.target.value })} />
                                    <input type="number" className="input-group-text col" placeholder="Rows Count" value={this.state.limit} onChange={(e) => this.setState({ limit: e.target.value })} />
                                </div>
                                <div className='row'>
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th scope="col" style={{color:'white'}}>Title</th>
                                                <th scope="col" style={{color:'white'}}>Genres</th>
                                                <th scope="col"  style={{color:'white'}}><i class="fa-solid fa-caret-up" onClick={this.sortPopularityDesc}></i>Popularity<i class="fa-solid fa-caret-down" onClick={this.sortPopularityAsc}></i></th>
                                                <th scope="col"  style={{color:'white'}}><i class="fa-solid fa-caret-up" onClick={this.sortRatingDesc}></i>Rating<i class="fa-solid fa-caret-down" onClick={this.sortRatingAsc}></i></th>
                                                <th scope="col"  style={{color:'white'}}></th>

                                            </tr>
                                        </thead>
                                        <tbody>
                                            {
                                                filterarr.map((movieObj) => (
                                                    <tr>
                                                        <td style={{color:'white'}}><img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`} alt={movieObj.title} style={{ width: '5rem' }} /> {movieObj.original_title}</td>

                                                        <td style={{color:'white'}}>{genreids[movieObj.genre_ids[0]]}</td>
                                                        <td style={{color:'white'}}>{movieObj.popularity} </td>
                                                        <td style={{color:'white'}}>{movieObj.vote_average} </td>
                                                        <td><button type="button" class="btn btn-danger" onClick={() => this.handleDelete(movieObj.id)}>Delete</button></td>
                                                        {/* <td style={{color: 'white'}}>{genreids[movieObj.genre_ids[0]]}</td>
                                                    <td style={{color: 'white'}}>{movieObj.popularity}</td>
                                                    <td style={{color: 'white'}}>{movieObj.vote_average}</td>
                                                    <td style={{color: 'white'}}><img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`} alt={movieObj.title} style={{width:'5rem'}}/> {movieObj.original_title}</td>
                                                    <td><button type="button" class="btn btn-danger" onClick={()=>this.handleDelete(movieObj.id)}>Delete</button></td> */}
                                                    </tr>
                                                ))
                                            }

                                        </tbody>

                                    </table>
                                    <nav aria-label="Page navigation example">
                                        <ul class="pagination">
                                            {
                                                pagesarr.map((page) => (
                                                    <li class="page-item"><a class="page-link" onClick={() => this.handlePageChange(page)}>{page}</a></li>
                                                ))
                                            }

                                        </ul>
                                    </nav>
                                </div>

                            </div>
                        </div>
                    </div>
                </>
            </div>
        )
    }
}
